import { Popconfirm, Table, Button, message, Drawer } from "antd";
import React, { useContext, useEffect, useState } from "react";
import { axiosInstance, BASE_URL } from "../../utils/axiosIntance";
import file from "../../img/file.png";
import DropFileInput from "./dropFile";
import { useHistory } from "react-router-dom";
import { SebedimContext } from "../../context/sebedim";

const ActiveProducts = () => {
  const { dil } = useContext(SebedimContext);
  const history = useHistory();
  const [data, setData] = useState([]);
  const [openChange, setOpenChange] = useState(false);
  const [order, setOrder] = useState({});
  const [files, setFiles] = useState([]);
  const [active, setActive] = useState(false);
  const [code, setCode] = useState("");
  const [payBefore, setPayBefore] = useState("");
  const [payBeforeType, setPayBeforeType] = useState("");
  const [edit, setEdit] = useState(false);
  const [surat, setSurat] = useState(false);
  const [productEdit, setProductEdit] = useState();
  const [gosh, setGosh] = useState(false);
  const [categories, setCategories] = useState([]);
  const [materials, setMaterials] = useState([]);
  const [colors, setColors] = useState([]);
  const [length, setLength] = useState([]);
  const [width, setWidth] = useState([]);
  const [product, setProduct] = useState({
    name_en: "",
    name_ru: "",
    code: "",
    description_en: "",
    description_ru: "",
    razmer_en: "",
    razmer_ru: "",
  });
  useEffect(() => {
    getData();
    getCategories();
    getMaterials();
    getColors();
    getWidth();
    getLength();
  }, []);

  const getData = () => {
    axiosInstance
      .get("/api/product/all")
      .then((data) => {
        console.log("product", data.data);
        setData(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getCategories = () => {
    axiosInstance
      .get("/api/category/all")
      .then((data) => {
        setCategories(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getMaterials = () => {
    axiosInstance
      .get("/api/material/all")
      .then((data) => {
        setMaterials(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getColors = () => {
    axiosInstance
      .get("/api/color/all")
      .then((data) => {
        setColors(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getWidth = () => {
    axiosInstance
      .get("/api/width/all")
      .then((data) => {
        setWidth(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getLength = () => {
    axiosInstance
      .get("/api/length/all")
      .then((data) => {
        setLength(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const columns = [
    {
      title: dil === "tm" ? "№" : dil === "ru" ? "№" : "№",
      dataIndex: "code",
    },
    {
      title: dil === "tm" ? "Ady" : dil === "ru" ? "Имя" : "Name",
      dataIndex: "name_ru",
      render: (text, record) => (
        <div>{dil === "ru" ? record?.name_ru : record?.name_en}</div>
      ),
    },
    {
      title:
        dil === "tm" ? "Düşündiriş" : dil === "ru" ? "Описание" : "Description",
      dataIndex: "description_ru",
      render: (text, record) => (
        <div>
