import { Popconfirm, Table, Button, message, Drawer } from "antd";
import React, { useContext, useEffect, useState } from "react";
import { axiosInstance, BASE_URL } from "../../utils/axiosIntance";
import { useHistory } from "react-router-dom";
import { SebedimContext } from "../../context/sebedim";
import DropFileInput from "./dropFile";

const Product = () => {
  const { dil } = useContext(SebedimContext);
  const history = useHistory();
  const [data, setData] = useState([]);
  const [markets, setMarkets] = useState([]);
  const [brands, setBrands] = useState([]);
  const [categories, setCategories] = useState([]);
  const [subCategories, setSubCategories] = useState([]);
  const [order, setCategoryEdit] = useState({});
  const [edit, setEdit] = useState(false);
  const [gosh, setGosh] = useState(false);
  const [files, setFiles] = useState([]);
  const [videFiles, setVideoFiles] = useState([]);
  const [filter, setFilter] = useState({ active: true });
  const [product, setProduct] = useState({
    name_tm: "",
    name_en: "",
    name_ru: "",
    description_ru: "",
    description_tm: "",
    description_en: "",
    price: 0,
    usd_price: 0,
    is_discount: false,
    is_valyuta: false,
    discount: 0,
    discount_price: 0,
    discount_usd: 0,
    quantity: 0,
    on_hand: true,
    is_selected: false,
    is_favorite: false,
    is_top: false,
    code: "",
    is_moresale: false,
    link: "",
    orderNum: null,
    MarketId: 0,
    CategoryId: 0,
    SubCategoryId: 0,
    BrandId: 0,
  });
  useEffect(() => {
    getData();
    getMarkets();
    getSubCategories();
    getCategories();
    getBrands();
  }, []);
  useEffect(() => {
    getData();
  }, [filter]);

  const getData = () => {
    axiosInstance
      .get("/api/product/all", {
        params: {
          active: filter.active,
        },
      })
      .then((data) => {
        console.log(data.data);
        setData(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getMarkets = () => {
    axiosInstance
      .get("/api/market/all")
      .then((data) => {
        console.log(data.data);
        setMarkets(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getBrands = () => {
    axiosInstance
      .get("/api/brand/all")
      .then((data) => {
        console.log(data.data);
        setBrands(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getCategories = () => {
    axiosInstance
      .get("/api/category/all")
      .then((data) => {
        console.log(data.data);
        setCategories(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getSubCategories = () => {
    axiosInstance
      .get("/api/subCategory/all")
      .then((data) => {
        console.log(data.data);
        setSubCategories(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const columns = [
    {
      title: "OrderNo",
      dataIndex: "orderNum",
    },
    {
      title: dil === "tm" ? "Ady " : dil === "ru" ? "Имя " : "Name ",
      dataIndex: dil == "tm" ? "name_tm" : dil == "ru" ? "name_ru" : "name_en",
    },
    {
      title:
        dil === "tm"
          ? "Dushundirish "
          : dil === "ru"
          ? "Описаниа "
          : "Description ",
      dataIndex:
        dil == "tm"
          ? "description_tm"
          : dil == "ru"
          ? "description_ru"
          : "description_en",
    },
    {
      title: "Market",
      render: (text, record) => (
        <div className="flex flex-wrap ">
          <h1 className="w-full">Market: {record?.Market?.name_tm}</h1>
          <h1 className="w-full">Category: {record?.Category?.name_tm}</h1>
          <h1 className="w-full">
            SubCategory: {record?.SubCategory?.name_tm}
          </h1>
        </div>
      ),
    },

    {
      title: "Surat",
      render: (text, record) => (
        <div>
          <img src={BASE_URL + record?.img} />
        </div>
      ),
    },
    {
      title: dil === "tm" ? "Hereket" : dil === "ru" ? "Действие" : "Action",
      render: (text, record) => (
        <div className="flex gap-3 flex-wrap">
          <Button
            onClick={() => {
              setEdit(true);
              setProduct(record);
            }}
            type="primary"
          >
            {dil === "tm"
              ? "Maglumat"
              : dil === "ru"
              ? "Информация"
              : "Information"}
          </Button>

          {!record.active && (
            <Button
              onClick={() => {
                unDeleteCategory(record.id);
              }}
              type="primary"
            >
              {dil === "tm" ? "Aktive" : dil === "ru" ? "Актив" : "Aktive"}
            </Button>
          )}
          <Popconfirm
            placement="top"
            title={"Are you sure"}
            onConfirm={() =>
              record.active
                ? DeleteCategory(record.id)
                : DestroyCategory(record.id)
            }
            okText="Yes"
            cancelText="No"
          >
            <Button type="danger">
              {dil === "tm" ? "Öçürmek" : dil === "ru" ? "Удалит" : "Delete"}
            </Button>
          </Popconfirm>
        </div>
      ),
    },
  ];

  const DeleteCategory = (id) => {
    axiosInstance
      .patch("/api/product/delete/" + id)
      .then((data) => {
        console.log(data.data);
        message.success("Öçürildi!");
        getData();
      })
      .catch((err) => {
        console.log(err);
        message.warn("Gaytadan Barlan!");
      });
  };

  const unDeleteCategory = (id) => {
    axiosInstance
      .patch("/api/product/unDelete/" + id)
      .then((data) => {
        console.log(data.data);
        message.success("Aktiwlendi!");
        getData();
      })
      .catch((err) => {
        console.log(err);
        message.warn("Gaytadan Barlan!");
      });
  };

  const DestroyCategory = (id) => {
    axiosInstance
      .delete("/api/product/destroy/" + id)
      .then((data) => {
        console.log(data.data);
        message.success("Öçürildi!");
        getData();
      })
      .catch((err) => {
        console.log(err);
        message.warn("Gaytadan Barlan!");
      });
  };

  const editData = () => {
    const formData = new FormData();

    files.length > 0 && formData.append("img", files[0]);

    formData.append("name_tm", product.name_tm);
    formData.append("name_ru", product.name_ru);
    formData.append("name_en", product.name_en);
    formData.append("description_tm", product.description_tm);
    formData.append("description_ru", product.description_ru);
    formData.append("description_en", product.description_en);
    formData.append("price", product.price);
    formData.append("is_valyuta", product.is_valyuta);
    formData.append("usd_price", product.usd_price);
    formData.append("is_discount", product.is_discount);
    formData.append("discount", product.discount);
    formData.append("discount_price", product.discount_price);
    formData.append("discount_usd", product.discount_usd);
    formData.append("quantity", product.quantity);
    formData.append("on_hand", product.on_hand);
    formData.append("code", product.code);
    formData.append("link", product.link);
    formData.append("orderNum", product.orderNum);
    formData.append("is_moresale", product.is_moresale);
    formData.append("is_favorite", product.is_favorite);
    formData.append("is_selected", product.is_selected);
    formData.append("is_top", product.is_top);
    formData.append("MarketId", product.MarketId);
    formData.append("CategoryId", product.CategoryId);
    formData.append("SubCategoryId", product.SubCategoryId);
    formData.append("BrandId", product.BrandId);
    formData.append("id", product.id);

    axiosInstance
      .patch("/api/product/update", formData)
      .then((data) => {
        console.log(data.data);
        message.success("Maglumatlar Uytgedildi!");
        getData();
        setProduct({
          name_tm: "",
          name_en: "",
          name_ru: "",
          orderNum: null,
          MarketId: 0,
        });
        setEdit(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const CreateCategory = () => {
    if (
      product.name_tm &&
      product.name_ru &&
      product.name_en &&
      product.MarketId
    ) {
      const formData = new FormData();

      if (files.length > 0) {
        files.map((file) => {
          formData.append("img", file);
        });
      }

      formData.append("name_tm", product.name_tm);
      formData.append("name_ru", product.name_ru);
      formData.append("name_en", product.name_en);
      formData.append("description_tm", product.description_tm);
      formData.append("description_ru", product.description_ru);
      formData.append("description_en", product.description_en);
      formData.append("price", product.price);
      formData.append("is_valyuta", product.is_valyuta);
      formData.append("usd_price", product.usd_price);
      formData.append("is_discount", product.is_discount);
      formData.append("discount", product.discount);
      formData.append("discount_price", product.discount_price);
      formData.append("discount_usd", product.discount_usd);
      formData.append("quantity", product.quantity);
      formData.append("on_hand", product.on_hand);
      formData.append("code", product.code);
      formData.append("link", product.link);
      formData.append("orderNum", product.orderNum);
      formData.append("is_moresale", product.is_moresale);
      formData.append("is_favorite", product.is_favorite);
      formData.append("is_selected", product.is_selected);
      formData.append("is_top", product.is_top);
      formData.append("MarketId", product.MarketId);
      formData.append("CategoryId", product.CategoryId);
      formData.append("SubCategoryId", product.SubCategoryId);
      formData.append("BrandId", product.BrandId);

      axiosInstance
        .post("/api/product/create", formData)
        .then((data) => {
          console.log(data.data);
          message.success("Maglumatlar döredildi!");
          getData();
          setProduct({
            name_tm: "",
            name_en: "",
            name_ru: "",
            orderNum: null,
            MarketId: 0,
          });
          setGosh(false);
        })
        .catch((err) => {
          console.log(err);
        });
    } else {
      message.warn("Maglumatlary doly Girizin!");
    }
  };

  const onFileChange = (files) => {
    console.log(files);
    setFiles(files);
  };

  const onFileChangeVideo = (files) => {
    console.log(files);
    setVideoFiles(files);
  };

  return (
    <>
      <Drawer
        width={600}
        placement="right"
        closable={true}
        mask={true}
        maskClosable={true}
        onClose={() => setEdit(false)}
        visible={edit}
      >
        <select
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          value={product.MarketId}
          onChange={(e) => setProduct({ ...product, MarketId: e.target.value })}
        >
          <option disabled value={0}>
            Market
          </option>
          {markets?.map((item, i) => {
            return (
              <option key={"cattttt" + i} value={item?.id}>
                {dil == "tm"
                  ? item?.name_tm
                  : dil == "ru"
                  ? item?.name_ru
                  : item?.name_en}
              </option>
            );
          })}
        </select>
        <input
          value={product.name_tm}
          onChange={(e) => setProduct({ ...product, name_tm: e.target.value })}
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          type="text"
          placeholder={
            dil === "tm" ? "Ady tm" : dil === "ru" ? "Имя tm" : "Name tm"
          }
        />
        <input
          value={product.name_ru}
          onChange={(e) => setProduct({ ...product, name_ru: e.target.value })}
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          type="text"
          placeholder={
            dil === "tm" ? "Ady ru" : dil === "ru" ? "Имя ру" : "Name ru"
          }
        />
        <input
          value={product.name_en}
          onChange={(e) => setProduct({ ...product, name_en: e.target.value })}
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          type="text"
          placeholder={
            dil === "tm" ? "Ady en" : dil === "ru" ? "Имя ен" : "Name en"
          }
        />

        <input
          value={product.orderNum}
          onChange={(e) => setProduct({ ...product, orderNum: e.target.value })}
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          type="text"
          placeholder={
