import { Popconfirm, Table, Button, message, Drawer } from "antd";
import React, { useContext, useEffect, useState } from "react";
import { axiosInstance, BASE_URL } from "../../utils/axiosIntance";
import { useHistory } from "react-router-dom";
import { SebedimContext } from "../../context/sebedim";
import DropFileInput from "./dropFile";

const Product = () => {
  const { dil } = useContext(SebedimContext);
  const history = useHistory();
  const [data, setData] = useState([]);
  const [markets, setMarkets] = useState([]);
  const [brands, setBrands] = useState([]);
  const [categories, setCategories] = useState([]);
  const [subCategories, setSubCategories] = useState([]);
  const [order, setCategoryEdit] = useState({});
  const [edit, setEdit] = useState(false);
  const [gosh, setGosh] = useState(false);
  const [files, setFiles] = useState([]);
  const [filter, setFilter] = useState({ active: true });
  const [product, setProduct] = useState({
    name_tm: "",
    name_en: "",
    name_ru: "",
    description_ru: "",
    description_tm: "",
    description_en: "",
    price: 0,
    usd_price: 0,
    is_discount: false,
    discount: 0,
    discount_price: 0,
    discount_usd: 0,
    quantity: 0,
    on_hand: true,
    code: "",
    is_moresale: false,
    link: "",
    orderNum: null,
    MarketId: 0,
    CategoryId: 0,
    SubCategoryId: 0,
    BrandId: 0,
  });
  useEffect(() => {
    getData();
    getMarkets();
  }, []);
  useEffect(() => {
    getData();
  }, [filter]);

  const getData = () => {
    axiosInstance
      .get("/api/product/all", {
        params: {
          active: filter.active,
        },
      })
      .then((data) => {
        console.log(data.data);
        setData(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getMarkets = () => {
    axiosInstance
      .get("/api/market/all")
      .then((data) => {
        console.log(data.data);
        setMarkets(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getBrands = () => {
    axiosInstance
      .get("/api/brand/all")
      .then((data) => {
        console.log(data.data);
        setBrands(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getCategories = () => {
    axiosInstance
      .get("/api/category/all")
      .then((data) => {
        console.log(data.data);
        setBrands(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const getSubCategories = () => {
    axiosInstance
      .get("/api/subCategory/all")
      .then((data) => {
        console.log(data.data);
        setBrands(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const columns = [
    {
      title: dil === "OrderNo",
      dataIndex: "orderNum",
    },
    {
      title: dil === "tm" ? "Ady en" : dil === "ru" ? "Имя en" : "Name en",
      dataIndex: dil == "tm" ? "name_tm" : dil == "ru" ? "name_ru" : "name_en",
    },
    {
      title: "Market",
      render: (text, record) => <div>{record?.Market?.name_tm}</div>,
    },

    {
      title: "Surat",
      render: (text, record) => (
        <div>
          <img src={BASE_URL + record?.img} />
        </div>
      ),
    },
    {
      title: dil === "tm" ? "Hereket" : dil === "ru" ? "Действие" : "Action",
      render: (text, record) => (
        <div className="flex gap-3 flex-wrap">
          <Button
            onClick={() => {
              setEdit(true);
              setProduct(record);
            }}
            type="primary"
          >
            {dil === "tm"
              ? "Maglumat"
              : dil === "ru"
              ? "Информация"
              : "Information"}
          </Button>

          {!record.active && (
            <Button
              onClick={() => {
                unDeleteCategory(record.id);
              }}
              type="primary"
            >
              {dil === "tm" ? "Aktive" : dil === "ru" ? "Актив" : "Aktive"}
            </Button>
          )}
          <Popconfirm
            placement="top"
            title={"Are you sure"}
            onConfirm={() =>
              record.active
                ? DeleteCategory(record.id)
                : DestroyCategory(record.id)
            }
            okText="Yes"
            cancelText="No"
          >
            <Button type="danger">
              {dil === "tm" ? "Öçürmek" : dil === "ru" ? "Удалит" : "Delete"}
            </Button>
          </Popconfirm>
        </div>
      ),
    },
  ];

  const DeleteCategory = (id) => {
    axiosInstance
      .patch("/api/product/delete/" + id)
      .then((data) => {
        console.log(data.data);
        message.success("Öçürildi!");
        getData();
      })
      .catch((err) => {
        console.log(err);
        message.warn("Gaytadan Barlan!");
      });
  };

  const unDeleteCategory = (id) => {
    axiosInstance
      .patch("/api/product/unDelete/" + id)
      .then((data) => {
        console.log(data.data);
        message.success("Aktiwlendi!");
        getData();
      })
      .catch((err) => {
        console.log(err);
        message.warn("Gaytadan Barlan!");
      });
  };

  const DestroyCategory = (id) => {
    axiosInstance
      .delete("/api/product/destroy/" + id)
      .then((data) => {
        console.log(data.data);
        message.success("Öçürildi!");
        getData();
      })
      .catch((err) => {
        console.log(err);
        message.warn("Gaytadan Barlan!");
      });
  };

  const editData = () => {
    const formData = new FormData();

    files.length > 0 && formData.append("img", files[0]);

    formData.append("name_tm", product.name_tm);
    formData.append("name_ru", product.name_ru);
    formData.append("name_en", product.name_en);
    formData.append("orderNum", product.orderNum);
    formData.append("MarketId", product.MarketId);
    formData.append("id", product.id);

    axiosInstance
      .patch("/api/product/update", formData)
      .then((data) => {
        console.log(data.data);
        message.success("Maglumatlar Uytgedildi!");
        getData();
        setProduct({
          name_tm: "",
          name_en: "",
          name_ru: "",
          orderNum: null,
          MarketId: 0,
        });
        setEdit(false);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const CreateCategory = () => {
    if (
      product.name_tm &&
      product.name_ru &&
      product.name_en &&
      product.MarketId
    ) {
      const formData = new FormData();

      files.length > 0 && formData.append("img", files[0]);

      formData.append("name_tm", product.name_tm);
      formData.append("name_ru", product.name_ru);
      formData.append("name_en", product.name_en);
      // formData.append("orderNum", product.orderNum);
      formData.append("MarketId", product.MarketId);

      axiosInstance
        .post("/api/product/create", formData)
        .then((data) => {
          console.log(data.data);
          message.success("Maglumatlar döredildi!");
          getData();
          setProduct({
            name_tm: "",
            name_en: "",
            name_ru: "",
            orderNum: null,
            MarketId: 0,
          });
          setGosh(false);
        })
        .catch((err) => {
          console.log(err);
        });
    } else {
      message.warn("Maglumatlary doly Girizin!");
    }
  };

  const onFileChange = (files) => {
    console.log(files);
    setFiles(files);
  };

  return (
    <>
      <Drawer
        width={500}
        placement="right"
        closable={true}
        mask={true}
        maskClosable={true}
        onClose={() => setEdit(false)}
        visible={edit}
      >
        <select
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          value={product.MarketId}
          onChange={(e) => setProduct({ ...product, MarketId: e.target.value })}
        >
          <option disabled value={0}>
            Market
          </option>
          {markets?.map((item, i) => {
            return (
              <option key={"cattttt" + i} value={item?.id}>
                {dil == "tm"
                  ? item?.name_tm
                  : dil == "ru"
                  ? item?.name_ru
                  : item?.name_en}
              </option>
            );
          })}
        </select>
        <input
          value={product.name_tm}
          onChange={(e) => setProduct({ ...product, name_tm: e.target.value })}
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          type="text"
          placeholder={
            dil === "tm" ? "Ady tm" : dil === "ru" ? "Имя tm" : "Name tm"
          }
        />
        <input
          value={product.name_ru}
          onChange={(e) => setProduct({ ...product, name_ru: e.target.value })}
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          type="text"
          placeholder={
            dil === "tm" ? "Ady ru" : dil === "ru" ? "Имя ру" : "Name ru"
          }
        />
        <input
          value={product.name_en}
          onChange={(e) => setProduct({ ...product, name_en: e.target.value })}
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          type="text"
          placeholder={
            dil === "tm" ? "Ady en" : dil === "ru" ? "Имя ен" : "Name en"
          }
        />

        <input
          value={product.orderNum}
          onChange={(e) => setProduct({ ...product, orderNum: e.target.value })}
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          type="text"
          placeholder={
            dil === "tm" ? "Tertip No" : dil === "ru" ? "Order No" : "Order No"
          }
        />
        <DropFileInput onFileChange={(files) => onFileChange(files)} />

        <button
          onClick={() => {
            editData();
          }}
          className={`${"!bg-blue !text-white "} bg-[#DEE6F9] h-[50px] rounded-[5px] w-full mt-4 text-[18px] font-sans text-blue`}
        >
          {dil === "tm" ? "Ugrat" : dil === "ru" ? "Отправлять" : "Send"}
        </button>
      </Drawer>

      <Drawer
        width={500}
        placement="right"
        closable={true}
        mask={true}
        maskClosable={true}
        onClose={() => setGosh(false)}
        visible={gosh}
      >
        <select
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          value={product.MarketId}
          onChange={(e) => setProduct({ ...product, MarketId: e.target.value })}
        >
          <option disabled value={0}>
            Market
          </option>
          {markets?.map((item, i) => {
            return (
              <option key={"cattttt" + i} value={item?.id}>
                {dil == "tm"
                  ? item?.name_tm
                  : dil == "ru"
                  ? item?.name_ru
                  : item?.name_en}
              </option>
            );
          })}
        </select>
        <input
          value={product.name_tm}
          onChange={(e) => setProduct({ ...product, name_tm: e.target.value })}
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          type="text"
          placeholder={
            dil === "tm" ? "Ady tm" : dil === "ru" ? "Имя tm" : "Name tm"
          }
        />
        <input
          value={product.name_ru}
          onChange={(e) => setProduct({ ...product, name_ru: e.target.value })}
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          type="text"
          placeholder={
            dil === "tm" ? "Ady ru" : dil === "ru" ? "Имя ру" : "Name ru"
          }
        />
        <input
          value={product.name_en}
          onChange={(e) => setProduct({ ...product, name_en: e.target.value })}
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          type="text"
          placeholder={
            dil === "tm" ? "Ady en" : dil === "ru" ? "Имя ен" : "Name en"
          }
        />

        <DropFileInput onFileChange={(files) => onFileChange(files)} />

        <button
          onClick={() => {
            CreateCategory();
          }}
          className={`${"!bg-blue !text-white "} bg-[#DEE6F9] h-[50px] rounded-[5px] w-full mt-4 text-[18px] font-sans text-blue`}
        >
          {dil === "tm" ? "Ugrat" : dil === "ru" ? "Отправлять" : "Send"}
        </button>
      </Drawer>

      <div className="w-full h-[50px] gap-4 p-0 flex justify-between ">
        <h2 className="leading-[50px] ml-[50px] text-sans text-[24px]">
          {dil === "tm" ? "Haryt" : dil === "ru" ? "Продукты" : "Products"}
        </h2>
        <select
          className="h-[50px] rounded-[6px] bg-[#FFFFFF] w-[100%] border-[1px] border-[#E3E7EE] text-[#999999] pl-[12px] text-[16px] font-sans mb-4"
          value={filter.active}
          onChange={(e) => setFilter({ ...filter, active: e.target.value })}
        >
          <option key={"cataktiwwe"} value={true}>
            Active
          </option>
          <option key={"catDisactive"} value={false}>
            Disactive
          </option>
        </select>
        <Button
          onClick={() => {
            setGosh(true);
          }}
          className="h-[40px] mt-[10px] mr-[50px] !rounded-[12px]"
          type="primary"
        >
          {dil === "tm" ? " Goşmak" : dil === "ru" ? "Добавить " : "Add "}
        </Button>
      </div>
      <Table
        columns={columns}
        dataSource={data}
        pagination={{
          pageSize: 50,
        }}
        scroll={{
          y: "72vh",
        }}
      />
    </>
  );
};

export default Product;
