import { Popconfirm, Table, Button, message, Drawer } from "antd";
import React, { useContext, useEffect, useState } from "react";
import { axiosInstance, BASE_URL } from "../../utils/axiosIntance";
import { useHistory } from "react-router-dom";
import { SebedimContext } from "../../context/sebedim";
import DropFileInput from "./dropFile";

const Product = () => {
  const { dil } = useContext(SebedimContext);
  const history = useHistory();
  const [data, setData] = useState([]);
  const [markets, setMarkets] = useState([]);
  const [order, setCategoryEdit] = useState({});
  const [edit, setEdit] = useState(false);
  const [gosh, setGosh] = useState(false);
  const [files, setFiles] = useState([]);
  const [filter, setFilter] = useState({ active: true });
  const [category, setCategory] = useState({
    name_tm: "",
    name_en: "",
    name_ru: "",
    orderNum: null,
    MarketId: 0,
  });
  useEffect(() => {
    getData();
    getMarkets();
  }, []);
  useEffect(() => {
    getData();
  }, [filter]);

  const getData = () => {
    axiosInstance
      .get("/api/category/all", {
        params: {
          active: filter.active,
        },
      })
      .then((data) => {
        console.log(data.data);
        setData(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getMarkets = () => {
    axiosInstance
      .get("/api/market/all")
      .then((data) => {
        console.log(data.data);
        setMarkets(data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const columns = [
    {
      title: dil === "OrderNo",
      dataIndex: "orderNum",
    },
    {
      title: dil === "tm" ? "Ady en" : dil === "ru" ? "Имя en" : "Name en",
      dataIndex: dil == "tm" ? "name_tm" : dil == "ru" ? "name_ru" : "name_en",
    },
    {
      title: "Market",
      render: (text, record) => <div>{record?.Market?.name_tm}</div>,
    },

    {
      title: "Surat",
      render: (text, record) => (
        <div>
          <img src={BASE_URL + record?.img} />
        </div>
      ),
    },
    {
      title: dil === "tm" ? "Hereket" : dil === "ru" ? "Действие" : "Action",
      render: (text, record) => (
        <div className="flex gap-3 flex-wrap">
          <Button
            onClick={() => {
              setEdit(true);
              setCategory(record);
            }}
            type="primary"
          >
            {dil === "tm"
              ? "Maglumat"
              : dil === "ru"
              ? "Информация"
              : "Information"}
          </Button>

          {!record.active && (
            <Button
              onClick={() => {
                unDeleteCategory(record.id);
              }}
              type="primary"
            >
              {dil === "tm" ? "Aktive" : dil === "ru" ? "Актив" : "Aktive"}
            </Button>
          )}
          <Popconfirm
            placement="top"
            title={"Are you sure"}
            onConfirm={() =>
              record.active
                ? DeleteCategory(record.id)
                : DestroyCategory(record.id)
            }
            okText="Yes"
            cancelText="No"
          >
            <Button type="danger">
              {dil === "tm" ? "Öçürmek" : dil === "ru" ? "Удалит" : "Delete"}
            </Button>
          </Popconfirm>
        </div>
      ),
    },
  ];

  const DeleteCategory = (id) => {
    axiosInstance
      .patch("/api/category/delete/" + id)
      .then((data) => {
        console.log(data.data);
        message.success("Öçürildi!");
        getData();
      })
      .catch((err) => {
        console.log(err);
        message.warn("Gaytadan Barlan!");
      });
  };

  const unDeleteCategory = (id) => {
    axiosInstance
      .patch("/api/category/unDelete/" + id)
      .then((data) => {
        console.log(data.data);
        message.success("Aktiwlendi!");
        getData();
      })
      .catch((err) => {
        console.log(err);
        message.warn("Gaytadan Barlan!");
      });
  };

  const DestroyCategory = (id) => {
    axiosInstance
      .delete("/api/category/destroy/" + id)
      .then((data) => {
        console.log(data.data);
        message.success("Öçürildi!");
        getData();
      })
      .catch((err) => {
        console.log(err);
        message.warn("Gaytadan Barlan!");
      });
  };

  const editData = () => {
    const formData = new FormData();

    files.length > 0 && formData.append("img", files[0]);

    formData.append("name_tm", category.name_tm);
    formData.append("name_ru", category.name_ru);
