          type="text"
          placeholder={
            dil === "tm"
              ? "Stokdaky Sany"
              : dil === "ru"
              ? "Каличиства в Сток"
              : "Stock quantity"
          }
        />

        <input
          value={product.massa}
          onChange={(e) => setProduct({ ...product, massa: e.target.value })}
