import React, { useContext, useState } from "react";
import {
  MailFilled,
  LockFilled,
  EyeFilled,
  EyeInvisibleFilled,
  PhoneFilled,
} from "@ant-design/icons";
import { message } from "antd";
import "antd/dist/antd.css";
import "./login.css";
import { axiosInstance } from "../../utils/axiosIntance";
import { useHistory } from "react-router-dom";
import { SebedimContext } from "../../context/sebedim";

const Login = () => {
  const { dil } = useContext(SebedimContext);
  const history = useHistory();
  const [phonenumber, setPhonenumber] = useState("+993");
  const [code, setCode] = useState("");
  const [type, setType] = useState("phone");
  const CheckPassword = () => {
    axiosInstance
      .post("/api/admin/login", {
        phonenumber: phonenumber,
      })
      .then((data) => {
        console.log(data.data);
        if (data.data.login === false) {
          message.warn(data.data.msg);
        } else {
          message.success("Ustunlikli!");
          localStorage.setItem("TDYAdmin", JSON.stringify(data.data));
          history.push({
            pathname: "/requests",
          });
        }
      })
      .catch((err) => {
        console.log(err);
        message.warn(err.msg);
      });
  };
  return (
    <div className="body">
      {/* <h2 className='logo' >Logo</h2> */}
      <div className="login-container mt-10 rounded-[8px]">
        <h2 className="h2">
          {dil === "tm"
            ? "Gaýtadan hoş geldiňiz"
            : dil === "ru"
            ? "Добро пожаловать"
            : "Welcome Back"}
        </h2>
        <p className="p">
          {dil === "tm"
            ? "Girmek üçin şahsyýet maglumatlaryňyzy giriziň!"
            : dil === "ru"
            ? "Введите свои учетные данные для доступа!"
            : "Enter your credentials to access!"}
        </p>

        <div className="input-container items-center flex">
          <PhoneFilled className="icon rotate-[100deg]" />
          <input
            className="input items-center flex"
            value={phonenumber}
            type="text"
            autoFocus
            style={{ width: "395px" }}
            placeholder={"Telefon belgi"}
            onChange={(e) => {
              e.target.value.length >= 4 &&
                e.target.value.length <= 12 &&
                e.target.value.slice(0, 4) == "+993" &&
                setPhonenumber(e.target.value);
            }}
            required
          />
        </div>

        <button className="button" onClick={() => CheckPassword()}>
          {dil === "tm" ? "Giriň" : dil === "ru" ? "Войти" : "Login"}
        </button>
      </div>
    </div>
  );
};

export default Login;
