        </div>

        <div className="flex gap-3 mb-4 justify-start items-start w-full cursor-pointer select-none">
          <input
            type="checkbox"
            id="is_moresale"
            name="is_moresale"
            className="w-[20px] h-[20px]"
            checked={product.is_moresale}
            onClick={() =>
              setProduct({ ...product, is_moresale: !product.is_moresale })
