            onClick={() => setProduct({ ...product, is_top: !product.is_top })}
          />
          <label className="cursor-pointer" htmlFor="is_top">
            Haryt Top my?
          </label>
        </div>

        <h1>Surat</h1>
        <DropFileInput onFileChange={(files) => onFileChange(files)} />
